import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Protectedroute = ({ children }) => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to handle invalid token
  const handleInvalidToken = () => {
    // Handle invalid token, e.g., navigate to login page
    navigate("/login");
  };

  // Function to handle server error
  const handleServerError = () => {
    // Handle server error, e.g., show error message to user
    console.error('Error validating token: Server Error');
  };

  useEffect(() => {
    const userToken = window.localStorage.getItem('token');
  
    if (!userToken) {
      navigate("/login");
    } else {
      fetch('/api/validate-token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${userToken}`
        },
        body: JSON.stringify({ token: userToken })
      })
      .then(response => {
        if (response.ok) {
          // Token is valid
          setIsLoggedIn(true);
        } else {
          // Token is invalid
          handleInvalidToken();
        }
      })
      .catch(error => {
        console.error('Error validating token:', error);
        handleServerError();
      });
    }
  }, [navigate]);

  return isLoggedIn ? children : null;
};

export default Protectedroute;
